import React, { useState } from 'react';
import axios from 'axios';
import * as S from './StyledHome';
import { useHistory } from 'react-router-dom';

function App(props) {
  const history = useHistory();
  const [ pokemon, setUsuario ] = useState('');
  const [ erro, setErro ] = useState(false);

  function handlePesquisa() {
    axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemon} /`)
      .then(response => {
        const pokemon = response.data;
        const pokemonName = [];
        pokemon.map((pokemon) => {
          pokemonName.push(pokemon.name);
        });
        localStorage.setItem('pokemonName', JSON.stringify(pokemonName));
        setErro(false);
        history.push('/Pokemon');
      })
      .catch(err => {
        setErro(true);
      });
  }

  return (
    <S.HomeContainer>
      <S.Content>
        <S.Input className="pokemonInput" placeholder="Pesquise um Pokémon" value={pokemon} onChange={e => setUsuario(e.target.value)} />
        <S.Button type="button" onClick={handlePesquisa}>Pesquisar</S.Button>
      </S.Content>
      { erro ? <S.ErrorMsg>Ocorreu um erro. Tente novamente.</S.ErrorMsg> : '' }
      <S.Content>
        
      </S.Content>
    </S.HomeContainer>
  );
}
function createCard () {
  card = `
    <div class="pokemon-picture">
      <img src="${pokemon.sprites.front_default}" alt="Sprite of ${pokemon.name}">
    </div>
    <div class="pokemon-info">
        <h1 class="name">Name: ${pokemon.name}</h1>
        <h2 class="number">Nº ${pokemon.id}</h2>
        <h3 class="type">Type: ${pokemon.types.map(item => ' ' + item.type.name).toString()}</h3>
        <h3 class="skill">Skills: ${pokemon.moves.map(item => ' ' + item.move.name).toString()}</h3>
        <h3 class="weight">Weight: ${pokemon.weight  / 10}kg</h3>
        <h3 class="height">Height: ${pokemon.height  / 10}m</h3>
    </div>`;
  return card;
};
export default App;
