import React, { useState } from 'react';
import axios from 'axios';
import * as S from './StyledHome';
import { useHistory } from 'react-router-dom';

function App(props) {
  const history = useHistory();
  const [ pokemon, setUsuario ] = useState('');
  const [ erro, setErro ] = useState(false);

  function handlePesquisa() {
    axios.get(`https://pokeapi.co/api/v2/pokemon/${buscapokemon} /`)
      .then(response => {
        const pokemon = response.data;
        const pokemonName = [];
        pokemon.map((pokemon) => {
          pokemonName.push(pokemon.name);
        });
        localStorage.setItem('pokemonName', JSON.stringify(pokemonName));
        setErro(false);
        history.push('/Pokemon');
      })
      .catch(err => {
        setErro(true);
      });
  }

  return (
    <S.HomeContainer>
      <S.Content>
        <S.Input className="pokemonInput" placeholder="Pesquise um PokÃ©mon" value={buscapokemon} onChange={e => setUsuario(e.target.value)} />
        <S.Button type="button" onClick={handlePesquisa}>Pesquisar</S.Button>
      </S.Content>
      { erro ? <S.ErrorMsg>Ocorreu um erro. Tente novamente.</S.ErrorMsg> : '' }
      <S.Content>
        
      </S.Content>
    </S.HomeContainer>
  );
}

export default App;
