import React, { useState } from 'react';
import axios from 'axios';
import * as S from './StyledHome';
import { useHistory } from 'react-router-dom';

function App(props) {
  const history = useHistory();
  const [ pokemon, setPokemon ] = useState('');
  const [ erro, setErro ] = useState(false);

  function handlePesquisa() {
    axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemon} /`)
      .then(response => {
        const pokemon = response.data;
        const pokemonName = [];
        pokemon.map((pokemon) => {
          pokemonName.push(pokemon.name);
        });
        localStorage.setItem('pokemonName', JSON.stringify(pokemonName));
        setErro(false);
        history.push('/Pokemon');
      })
      .catch(err => {
        setErro(true);
      });
  }

  return (
    <S.HomeContainer>
      <S.Content>
        <S.Input className="pokemonInput" placeholder="Pesquise um Pokémon" value={pokemon} onChange={e => setPokemon(e.target.value)} />
        <S.Button type="button" onClick={handlePesquisa}>Pesquisar</S.Button>
      </S.Content>
      { erro ? <S.ErrorMsg>Ocorreu um erro. Tente novamente.</S.ErrorMsg> : '' }
    </S.HomeContainer>
  );
}


function renderCard(card) {
  const div = document.createElement("div");
  div.style.width = "20rem";
  div.style.margin = "2rem";
  div.className = "card";
  div.innerHTML = `
  <img src="${card.photo}" class="card-img-top" alt="${card.name}" />
  <div class="card-body">
    <h5 class="card-title">${card.name}</h5>
    <p class="card-text">
      Tipo: ${card.property_type}
    </p>
    <p class="card-text">
    Preço: R$${card.price},00
  </p>
  </div>
`;
  cardsContainer.appendChild(div);
}

async function main() {
  data = await fetchCards();
  if(data) {
    renderCards(data);
  }
}

main();v
