import React, { useState } from 'react';
import axios from 'axios';
import * as S from './StyledHome';
//import { useHistory } from 'react-router-dom';
import { Container, Row } from 'reactstrap';
import {
    Card, CardImg, CardText, CardTitle
  } from 'reactstrap';

function App(props) {
//  const history = useHistory();
  const [ pokemon, setPokemon ] = useState([]);
//  const [ erro, setErro ] = useState(false);

  async function getPokemon() {
    const pokemons = await axios.get("https://pokeapi.co/api/v2/pokemon?limit=151");
  
    const promises = pokemons.data.results.map(async (pokeUrl) => {
        const result = await axios.get(pokeUrl.url).then((rp) => rp.data);
        return result;
    });

    const pokeData = await Promise.all(promises);

      setPokemon(pokeData);
  }

  useEffect(() => {
    getPokemon();
}, []);

  function handlePesquisa() {
    axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemon} /`)
      .then(response => {
        const pokemon = response.data;
        const pokemonName = [];
        pokemon.map((pokemon) => {
          pokemonName.push(pokemon.name);
        });
        localStorage.setItem('pokemonName', JSON.stringify(pokemonName));
        setErro(false);
        history.push('/Pokemon');
      })
      .catch(err => {
        setErro(true);
      });
  }

  return (
    <S.HomeContainer>
      <S.Content>
        <S.Input className="pokemonInput" placeholder="Pesquise um PokÃ©mon" value={pokemon} onChange={e => setPokemon(e.target.value)} />
        <S.Button type="button" onClick={handlePesquisa}>Pesquisar</S.Button>
      </S.Content>
      { erro ? <S.ErrorMsg>Ocorreu um erro. Tente novamente.</S.ErrorMsg> : '' }
    </S.HomeContainer>
  
    
  );
}

export default App;
